BluetoothCharacteristic.prototype.set = function (value, callback) {
  this.log.info(this.prefix, "Set | " + value);
  var buffer = this.toBuffer(value);
  this.nobleCharacteristic.write(buffer, false);
  callback();
};


BluetoothCharacteristic.prototype.notify = function (buffer, notification) {
  if (notification) {
    var value = this.fromBuffer(buffer);
    this.log.info(this.prefix, "Notify | " + value);
    this.homebridgeCharacteristic.updateValue(value, null, this);
  }
};


BluetoothCharacteristic.prototype.toBuffer = function (value) {
  var buffer;
  switch (this.homebridgeCharacteristic.props['format']) {
    case Characteristic.Formats.BOOL: // BLECharCharacteristic
      buffer = Buffer.alloc(1);
      buffer.writeInt8(value ? 1 : 0, 0);
      break;
    case Characteristic.Formats.INT: // BLEIntCharacteristic
      buffer = Buffer.alloc(4);
      buffer.writeInt32LE(value, 0);
      break;
    case Characteristic.Formats.FLOAT: // BLEFloatCharacteristic
      buffer = Buffer.alloc(4);
      buffer.writeFloatLE(value, 0);
      break;
    case Characteristic.Formats.STRING: // BLECharacteristic
      buffer = Buffer.from(value, 'utf8');
      break;
    case Characteristic.Formats.UINT8: // BLEUnsignedCharCharacteristic
      buffer = Buffer.alloc(1);
      buffer.writeUInt8(value, 0);
      break;
    case Characteristic.Formats.UINT16: // BLEUnsignedShortCharacteristic
      buffer = Buffer.alloc(2);
      buffer.writeUInt16(value, 0);
      break;
    case Characteristic.Formats.UINT32: // BLEUnsignedIntCharacteristic
      buffer = Buffer.alloc(4);
      buffer.writeUInt32(value, 0);
      break;
    case Characteristic.Formats.UINT64: // BLEUnsignedLongCharacteristic
      buffer = Buffer.alloc(8);
      buffer.writeUIntLE(value, 0, 8);
      break;
    default:
      this.log.error(this.prefix, "Unsupported data conversion | " +
                     this.homebridgeCharacteristic.props['format']);
      buffer = Buffer.alloc(1);
      buffer.writeInt8(0, 0);
      break;
  }
  return buffer;
};


BluetoothCharacteristic.prototype.fromBuffer = function (buffer) {
  var value;
  switch (this.homebridgeCharacteristic.props['format']) {
    case Characteristic.Formats.BOOL: // BLECharCharacteristic
      value = buffer.readInt8(0);
      break;
    case Characteristic.Formats.INT: // BLEIntCharacteristic
      value = buffer.readInt32LE(0);
      break;
    case Characteristic.Formats.FLOAT: // BLEFloatCharacteristic
      value = buffer.readFloatLE(0);
      break;
    case Characteristic.Formats.STRING: // BLECharacteristic
      value = buffer.toString('utf8', 0);
      break;
    case Characteristic.Formats.UINT8: // BLEUnsignedCharCharacteristic
      value = buffer.readUInt8(0);
      break;
    case Characteristic.Formats.UINT16: // BLEUnsignedShortCharacteristic
      value = buffer.readUInt16LE(0);
      break;
    case Characteristic.Formats.UINT32: // BLEUnsignedIntCharacteristic
      value = buffer.readUInt32LE(0);
      break;
    case Characteristic.Formats.UINT64: // BLEUnsignedLongCharacteristic
      value = buffer.readUIntLE(0, 8);
      break;
    default:
      value = 0;
      this.log.error(this.prefix, "Unsupported data conversion | " +
                     this.homebridgeCharacteristic.props['format']);
  }
  return value;
};


BluetoothCharacteristic.prototype.disconnect = function () {
  if (this.nobleCharacteristic && this.homebridgeCharacteristic) {
    if (this.nobleCharacteristic.properties.indexOf('read') >= 0) {
      this.homebridgeCharacteristic.removeAllListeners('get');
    }
    if (this.nobleCharacteristic.properties.indexOf('write') >= 0) {
      this.homebridgeCharacteristic.removeAllListeners('set');
    }
    if (this.nobleCharacteristic.properties.indexOf('notify') >= 0) {
      this.nobleCharacteristic.unsubscribe(null);
      this.nobleCharacteristic.removeAllListeners('read');
    }
    this.homebridgeCharacteristic = null;
    this.nobleCharacteristic = null;
    this.log.info(this.prefix, "Disconnected");
  }
};

var Noble, UUIDGen, Accessory, BluetoothAccessory;

module.exports = function (noble, uuidGen, accessory, bluetoothAccessory) {
  Noble = noble;
  UUIDGen = uuidGen
  Accessory = accessory;
  BluetoothAccessory = bluetoothAccessory;

  return BluetoothPlatform;
};


function BluetoothPlatform(log, config, homebridgeAPI) {
  this.log = log;

  if (!config.accessories || !(config.accessories instanceof Array)) {
    throw new Error("Missing mandatory config 'accessories'");
  }
  this.bluetoothAccessories = {};
  for (var accessoryConfig of config.accessories) {
    var accessoryAddress = trimAddress(accessoryConfig.address);
    var bluetoothAccessory = new BluetoothAccessory(this.log, accessoryConfig);
    this.bluetoothAccessories[accessoryAddress] = bluetoothAccessory;
  }
  this.cachedHomebridgeAccessories = {};

  this.homebridgeAPI = homebridgeAPI;
  this.homebridgeAPI.on('didFinishLaunching', this.didFinishLaunching.bind(this));
}


BluetoothPlatform.prototype.configureAccessory = function (homebridgeAccessory) {
  var accessoryAddress = homebridgeAccessory.context['address'];
  var bluetoothAccessory = this.bluetoothAccessories[accessoryAddress];
  if (!bluetoothAccessory) {
    this.log.debug("Removed | " + homebridgeAccessory.displayName + " (" + accessoryAddress + ")");
    this.homebridgeAPI.unregisterPlatformAccessories("homebridge-bluetooth", "Bluetooth",
                                                     [homebridgeAccessory]);
    return;
  }

  this.log.debug("Persist | " + homebridgeAccessory.displayName + " (" + accessoryAddress + ")");
  this.cachedHomebridgeAccessories[accessoryAddress] = homebridgeAccessory;
};


BluetoothPlatform.prototype.didFinishLaunching = function () {
  Noble.on('stateChange', this.stateChange.bind(this));
};


BluetoothPlatform.prototype.stateChange = function (state) {
  if (state != 'poweredOn') {
    this.log.info("Stopped | " + state);
    Noble.stopScanning();
  }

  this.log.info("Started | " + state);
  Noble.startScanning([], false);
  Noble.on('discover', this.discover.bind(this));
};


BluetoothPlatform.prototype.discover = function (nobleAccessory) {
  var accessoryAddress = trimAddress(nobleAccessory.address);
  var bluetoothAccessory = this.bluetoothAccessories[accessoryAddress];
  if (!bluetoothAccessory) {
    this.log.debug("Ignored | " + nobleAccessory.advertisement.localName +
                  " (" + nobleAccessory.address + ") | RSSI " + nobleAccessory.rssi + "dB");
    return;
  }

  this.log.debug("Discovered | " + nobleAccessory.advertisement.localName +
                " (" + nobleAccessory.address + ") | RSSI " + nobleAccessory.rssi + "dB");
  nobleAccessory.connect(function (error) {
    this.connect(error, nobleAccessory)
  }.bind(this));
};


BluetoothPlatform.prototype.connect = function (error, nobleAccessory) {
  if (error) {
    this.log.error("Connecting failed | " + nobleAccessory.advertisement.localName +
                   " (" + nobleAccessory.address + ") | " + error);
    return;
  }

  var accessoryAddress = trimAddress(nobleAccessory.address);
  var bluetoothAccessory = this.bluetoothAccessories[accessoryAddress];
  var homebridgeAccessory = this.cachedHomebridgeAccessories[accessoryAddress];
  if (!homebridgeAccessory) {
    homebridgeAccessory = new Accessory(bluetoothAccessory.name,
                                        UUIDGen.generate(bluetoothAccessory.name));
    homebridgeAccessory.context['address'] = accessoryAddress;
    this.homebridgeAPI.registerPlatformAccessories("homebridge-bluetooth", "Bluetooth",
                                                   [homebridgeAccessory]);
  } else {
    delete this.cachedHomebridgeAccessories[accessoryAddress];
  }

  bluetoothAccessory.connect(nobleAccessory, homebridgeAccessory);
  nobleAccessory.once('disconnect', function (error) {
    this.disconnect(nobleAccessory, homebridgeAccessory, error);
  }.bind(this));

  if (Object.keys(this.cachedHomebridgeAccessories).length > 0) {
    Noble.startScanning([], false);
  }
};


BluetoothPlatform.prototype.disconnect = function (nobleAccessory, homebridgeAccessory, error) {
  var accessoryAddress = trimAddress(nobleAccessory.address);
  this.cachedHomebridgeAccessories[accessoryAddress] = homebridgeAccessory;

  Noble.startScanning([], false);
};


function trimAddress(address) {
  return address.toLowerCase().replace(/:/g, "");
}

var Service, BluetoothCharacteristic;
var Chalk = require('chalk');

module.exports = function (service, bluetoothCharacteristic) {
  Service = service;
  BluetoothCharacteristic = bluetoothCharacteristic;

  return BluetoothService;
};


function BluetoothService(log, config, prefix) {
  this.log = log;

  if (!config.name) {
    throw new Error("Missing mandatory config 'name'");
  }
  this.name = config.name;
  this.prefix = prefix + " " + Chalk.magenta("[" + this.name + "]");

  if (!config.type) {
    throw new Error(this.prefix + " Missing mandatory config 'type'");
  }
  this.type = config.type;
  if (!Service[this.type]) {
    throw new Error(this.prefix + " Service type '" + this.type + "' is not defined. " +
                    "See 'HAP-NodeJS/lib/gen/HomeKitType.js' for options.")
  }
  this.class = Service[this.type]; // For example - Service.Lightbulb

  if (!config.UUID) {
    throw new Error(this.prefix + " Missing mandatory config 'UUID'");
  }
  this.UUID = config.UUID;

  if (!config.characteristics || !(config.characteristics instanceof Array)) {
    throw new Error(this.prefix + " Missing mandatory config 'characteristics'");
  }

  this.log.debug(this.prefix, "Initialized | Service." + this.type + " (" + this.UUID + ")");
  this.bluetoothCharacteristics = {};
  for (var characteristicConfig of config.characteristics) {
    var characteristicUUID = trimUUID(characteristicConfig.UUID);
    this.bluetoothCharacteristics[characteristicUUID] =
        new BluetoothCharacteristic(this.log, characteristicConfig, this.prefix);
  }

  this.homebridgeService = null;
  this.nobleService = null;
}


BluetoothService.prototype.connect = function (nobleService, homebridgeService) {
  this.log.info(this.prefix, "Connected");
  this.log.debug(this.prefix, "Service." + this.type + " (" + this.UUID + ")");
  this.homebridgeService = homebridgeService;

  this.nobleService = nobleService;
  this.nobleService.discoverCharacteristics([], this.discoverCharacteristics.bind(this));
};


BluetoothService.prototype.discoverCharacteristics = function (error, nobleCharacteristics) {
  if (error) {
    this.log.error(this.prefix, "Discover characteristics failed | " + error);
    return;
  }
  if (nobleCharacteristics.length == 0) {
    this.log.warn(this.prefix, "No characteristics discovered");
    return;
  }

  for (var nobleCharacteristic of nobleCharacteristics) {
    var characteristicUUID = trimUUID(nobleCharacteristic.uuid);
    var bluetoothCharacteristic = this.bluetoothCharacteristics[characteristicUUID];
    if (!bluetoothCharacteristic) {
      this.log.debug(this.prefix, "Ignored | Characteristic (" + nobleCharacteristic.uuid + ")");
      continue;
    }

    var homebridgeCharacteristic =
        this.homebridgeService.getCharacteristic(bluetoothCharacteristic.class);
    bluetoothCharacteristic.connect(nobleCharacteristic, homebridgeCharacteristic);
  }
};


BluetoothService.prototype.disconnect = function () {
  for (var characteristicUUID in this.bluetoothCharacteristics) {
    this.bluetoothCharacteristics[characteristicUUID].disconnect();
  }
  if (this.nobleCharacteristic && this.homebridgeCharacteristic) {
    this.homebridgeService = null;
    this.nobleService = null;
    this.log.info(this.prefix, "Disconnected");
  }
};


function trimUUID(uuid) {
  return uuid.toLowerCase().replace(/:/g, "").replace(/-/g, "");
}
